DROP TABLE IF EXISTS manufacturing CASCADE;
DROP TABLE IF EXISTS warehouse CASCADE;
DROP TABLE IF EXISTS headOffice CASCADE;
DROP TABLE IF EXISTS jobPosition CASCADE;
